//Write a Java program to find the sum of all elements in an array.
package com.assignments;

public class Arrays05 {

	public static void main(String[] args) {
		int[] a= {3,16,6,12,32};
		int sum=0;
		for(int i:a)
			sum+=i;
		System.out.println(sum);
		// TODO Auto-generated method stub

	}

}
